mapper.update:
UPDATE ALTO_CLUB
SET MEMBER_NUM = NVL(MEMBER_NUM, 0) + 1
WHERE CLUB_CODE = #{club_code}

UPDATE ALTO_CLUB
SET MEMBER_NUM = NVL(MEMBER_NUM, 0) - 1
WHERE CLUB_CODE = #{club_code}

UPDATE ALTO_CLUB
SET MEMBER_OUT = NVL(MEMBER_OUT, 0) + 1
WHERE CLUB_CODE = #{club_code}

mapper.select:

SELECT MANAGER
FROM ALTO_JOIN
WHERE MEM_ID = '${mem_id}' AND CLUB_CODE = ${club_code}

mapper.update:

UPDATE ALTO_JOIN
SET MANAGER = 'Y'
WHERE MEM_ID = '${mem_id}' AND CLUB_CODE = ${club_code}


UPDATE ALTO_CLUB
SET MANAGER = '${mem_id}'
WHERE CLUB_CODE = ${club_code}

mapper.insert:
UPDATE ALTO_CLUB 
SET AREA_CODE = #{area_code}, CATE_M = #{cate_m}, CATE_S = #{cate_s}, IMG = #{club_img}, INTRO = #{intro}, MEMBER_MAX = #{member_max}
WHERE CLUB_CODE = ${club_code}

mapper.select:
SELECT LIKE_CODE 
FROM ALTO_LIKE
WHERE CLUB_CODE = #{club_code} AND MEM_ID = #{mem_id}

SELECT A.club_code, A.title, A.cate_m, A.cate_m_name, A.cate_s, A.cate_s_name, A.area_name, A.member_num, A.img, B.SDATE schedule, B.SPLACE place FROM ( SELECT A.CLUB_CODE club_code, A.TITLE title, A.CATE_M cate_m, C.NAME cate_m_name, A.CATE_S cate_s, D.NAME cate_s_name, B.NAME area_name, A.MEMBER_NUM member_num, A.IMG img FROM ALTO_CLUB A, ALTO_AREA B, ALTO_HOBBY C, ALTO_HOBBY_SUB D WHERE A.AREA_CODE = B.AREA_CODE AND A.CATE_M = C.HOBBY_CODE AND A.CATE_S = D.HOBBY_SUB_CODE
AND c.HOBBY_CODE = #{hobbyC}
AND A.CATE_M IN
#{item}
AND A.CATE_S IN
#{item}
AND A.AREA_CODE IN
#{item}
AND (A.TITLE LIKE '%' || #{keyword} || '%' OR B.NAME LIKE '%' || #{keyword} || '%' OR C.NAME LIKE '%' || #{keyword} || '%' OR D.NAME LIKE '%' || #{keyword} || '%')
) A LEFT JOIN ALTO_CLUB_SCHEDULE B ON A.CLUB_CODE = B.CLUB_CODE
ORDER BY A.club_code DESC
ORDER BY A.REGIDATE DESC
ORDER BY A.REGIDATE ASC
ORDER BY B.schedule DESC
ORDER BY A.member_num DESC
ORDER BY A.member_num ASC
